import React, { useState } from 'react';
import { Col, Row, Label, Form, FormGroup, Input, ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';


const MyForm = () => {
    let state = {
        classroom: [
            {
                myClassName: "Class mane",
                mentor: "Joe Mentor",
                students: [ 
                    {
                        student: "Billy Bob",
                        averageGrade: "94",
                        assignments: [ 
                            {
                                assignmentName: "homework 1",
                                assignmentGrade: "assignment grade"

                            }
                        ]
                    }
                ]
                
            },
            {
                myClassName: "yo class haha",
                mentor: "The Good Mentor",
                students: [ 
                    {
                        student: "Sick memes bruh",
                        averageGrade: "20",
                        assignments: [ 
                            {
                                assignmentName: "homework 1",
                                assignmentGrade: "assignment grade"

                            }
                        ]
                    }
                ]
                
            }
        ]
    }

    const [dropdownOpen, setOpen] = useState(false);

    const toggle = () => setOpen(!dropdownOpen);
    const createSelectItems = () =>{
        let items = [];
        for (let i = 0; i <= this.state.maxValue; i++) {
            items.push(<option key={i}>{this.state.myClassName}</option>);
        }
        return items;
    }
    // onDropDownSelected(e) {
    //     console.log("")
    // }
    return (
            <Input type = "select" onChange={this.onDropdownSelected} label = "Multiple Select" multiple>
                {this.createSelectItems}
            </Input>
            // state.classroom.map((myClass, index) => {
            //     return(
            //         console.log(myClass.myClassName)
            //     );
            // })
    );
}

export default MyForm;
