import React, { useState } from 'react';
import { Col, Row, Label, Container, FormGroup, Input, Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';



const MyForm = (props) => {
    let state = {
        classroom: [
            {
                id: 12312,
                myClassName: "Class mane",
                mentor: "Joe Mentor",
                students: [ 
                    {
                        student: "Billy Bob",
                        averageGrade: "94",
                        assignments: [ 
                            {
                                assignmentName: "homework 1",
                                assignmentGrade: "assignment grade"

                            }
                        ]
                    }
                ]
                
            },
            {   
                id: 124121,
                myClassName: "yo class haha",
                mentor: "The Good Mentor",
                students: [ 
                    {
                        student: "Sick memes bruh",
                        averageGrade: "20",
                        assignments: [ 
                            {
                                assignmentName: "homework 1",
                                assignmentGrade: "assignment grade"

                            }
                        ]
                    }
                ]
                
            }
        ]
    }
    const [dropdownOpen, setDropdownOpen] = useState(false);


 
    const toggle = () => setDropdownOpen(prevState => !prevState);

    
    return (
        <Container>
            <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
                <DropdownToggle caret> 
                ahh
                </DropdownToggle>
                <DropdownMenu>
                <DropdownItem header>ALL</DropdownItem>
                <DropdownItem divider/>
            {
                state.classroom.map((mclass, index) => {
                    return (

                        <DropdownItem>mclass.myClassName</DropdownItem>
                           
                                
                    );
                })
            }
            </DropdownMenu>
            </Dropdown>
        </Container>
    );
}

export default MyForm;
