import React, { useState } from 'react';
import { Col, Row, Label, Container, FormGroup, Input, Form, FormText } from 'reactstrap';



const MyForm = (props) => {
    let state = {
        classroom: [
            {
                id: 12312,
                myClassName: "Class mane",
                mentor: "Joe Mentor",
                students: [ 
                    {
                        student: "Billy Bob",
                        averageGrade: "94",
                        assignments: [ 
                            {
                                assignmentName: "homework 1",
                                assignmentGrade: "assignment grade"

                            }
                        ]
                    }
                ]
                
            },
            {   
                id: 124121,
                myClassName: "yo class haha",
                mentor: "The Good Mentor",
                students: [ 
                    {
                        student: "Sick memes bruh",
                        averageGrade: "20",
                        assignments: [ 
                            {
                                assignmentName: "homework 1",
                                assignmentGrade: "assignment grade"

                            }
                        ]
                    }
                ]
                
            }
        ]
    }
    const [dropdownOpen, setDropdownOpen] = useState(false);

    const toggle = () => setDropdownOpen(prevState => !prevState);

    
    return (
        <Form>
            <FormGroup>
                <Row>
                    <Label for="selectMulti">Choose which classes to display!</Label>
                        <Input type="select" name="selectMulti" id="selectMulti" multiple>
                            {
                                state.classroom.map((mclass, index) => {
                                    return (
                                        <option key={mclass.id}>{mclass.myClassName}</option>
                                                
                                    );
                                })
                            }
                    </Input>
                </Row>
            </FormGroup>
            <FormGroup>
                <Row> 
                    <Col sm={6}>
                        <Label check>
                            <Input key="1" type="checkbox" />
                                grade vs assignments (per student)
                        </Label>
                    </Col>
                    <Col sm={6}>
                        <Label check>
                            <Input key="2" type="checkbox" />
                                average grade vs assignment (classrooms)
                        </Label>
                    </Col>
                </Row>
                <Row>
                    <Col sm={6}>
                        <Label check>
                            <Input key="3" type="checkbox" />
                                average grades vs classes (classrooms)
                        </Label>
                    </Col>
                    <Col sm={6}>
                        <Label check>
                            <Input key="4" type="checkbox" />
                                average grades of all classes
                        </Label>
                    </Col>              
                </Row>
                <Row>
                    <Col>
                        <Label check>
                            <Input key="5" type="checkbox" onClick={( )=>{console.log("lordie")}} />
                                All
                        </Label>
                    </Col>
                </Row>
            </FormGroup>
        </Form>
    );
}

export default MyForm;
