import React, { useState } from 'react';
import { Col, Row, Label, Form, FormGroup, Input, ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';


function myclassnames(props) {
    const classes = state.classroom;
    return (
        <ul>
        {
            classes.map((mclass) =>
                <p key = {mclass.id.toString()}
                        value = {mclass.myClassName} />
            )}
        </ul>
    )


}
const MyForm = (props) => {
    let state = {
        classroom: [
            {
                id: 12312,
                myClassName: "Class mane",
                mentor: "Joe Mentor",
                students: [ 
                    {
                        student: "Billy Bob",
                        averageGrade: "94",
                        assignments: [ 
                            {
                                assignmentName: "homework 1",
                                assignmentGrade: "assignment grade"

