import React, { useState } from 'react';
import { Col, Row, Label, Form, FormGroup, Input, ButtonDropdown, Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';


const MyForm = () => {
    let state = {
        classroom: [
            {
                myClassName: "Class mane",
                mentor: "Joe Mentor",
                students: [ 
                    {
                        student: "Billy Bob",
                        averageGrade: "94",
                        assignments: [ 
                            {
                                assignmentName: "homework 1",
                                assignmentGrade: "assignment grade"

                            }
                        ]
                    }
                ]
                
            },
            {
                myClassName: "yo class haha",
                mentor: "The Good Mentor",
                students: [ 
                    {
                        student: "Sick memes bruh",
                        averageGrade: "20",
                        assignments: [ 
                            {
                                assignmentName: "homework 1",
                                assignmentGrade: "assignment grade"

                            }
                        ]
                    }
                ]
                
            }
        ]
    }

    const [dropdownOpen, setOpen] = useState(false);

    const toggle = () => setOpen(!dropdownOpen);

    return (
        <Form>
            <FormGroup>
            <Dropdown isOpen={dropdownOpen} toggle={toggle}>
                <DropdownToggle caret> Dropdown </DropdownToggle>
                <DropdownMenu>
                    <DropdownItem header>Header</DropdownItem>
                    <DropdownItem>Some Action</DropdownItem>
                    <DropdownItem disabled>Action (disabled)</DropdownItem>
                    <DropdownItem divider />
                    
                    </DropdownMenu>
                </Dropdown>
            </FormGroup>
       </Form>
    );
}

export default MyForm;
