import React, { useState } from 'react';
import { Col, Row, Label, Form, FormGroup, Input, ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';


const MyForm = (props) => {
    let state = {
        classroom: [
            {
                className: "Class mane",
                mentor: "Joe Mentor",
                students: [ 
                    {
                        student: "Billy Bob",
                        assignments: [ 
                            {
                                assignmentName: "homework 1"

                            }
                        ]
                    }
                ]
                
            }
        ]
    }

    const [dropdownOpen, setOpen] = useState(false);

    const toggle = () => setOpen(!dropdownOpen);

    return (
        <div className="App">
        <div> Please choose your options! </div>

        <Form>
            <Row form>
            <Col>
                <FormGroup>
                    <ButtonDropdown isOpen={dropdownOpen} toggle={toggle}>
                        <DropdownToggle caret >
                            Choose which class you want to display!!
                        </DropdownToggle>
                        <DropdownMenu>
                            <DropdownItem>All</DropdownItem>
                            <DropdownItem divider />
                            <DropdownItem>Header</DropdownItem>
                            <DropdownItem>Another Action</DropdownItem>
                            <DropdownItem>Another Action</DropdownItem>
                        </DropdownMenu>
                    </ButtonDropdown>
                </FormGroup>
                </Col>
            </Row>
            <Row>
            <Col>
            <FormGroup>
                <Label check>
                    <Input type="checkbox" /> {" "}
                    All
                </Label>
                <Label check>
                    <Input type="checkbox" /> {" "}
                    Line chart (Avg Grade)
                </Label>
                <Label check>
                    <Input type="checkbox" /> {" "}
                    Bar graph (Grade vs Assignment)
                </Label>
                <Label check>
                    <Input type="checkbox" /> {" "}
                    Line chart (Avg Grade vs Time)
                </Label>
                <Label check>
                    <Input type="checkbox" /> {" "}
                    Line graph (students average per assessment)
                </Label>
            </FormGroup>
            
            </Col>
            </Row>
        </Form>

        </div>
    );
}

export default MyForm;
